import React, { useState, useEffect } from "react";
import axios from "axios";

const FormMovie = () => {
  const [movies, setMovies] = useState({
    title: "",
    poster: "",
    comment: "",
  });

  useEffect((event) => {
    event.preventDefault();
    const url = "https://post-a-form.herokuapp.com/api/movies/";
    axios
      .post(url, movies)
      .then((response) => response.data)
      .then((response) => {
        alert(`le film ajouté avec l'ID ${response.id} !`);
      })
      .catch((event) => {
        console.error(event);
        alert(`Erreur lors de l'ajout d'un film : ${event.message}`);
      });
  }, []);

  return (
    <div>
      <form onSubmit={useEffect}>
        <fieldset style={{ width: "50%", marginLeft: "25%", marginTop: "20%" }}>
          <legend>Ajouter un film</legend>
          <div>
            <label htmlFor="title">Nom du Film </label>
            <input
              type="text"
              id="title"
              name="title"
              value={movies.title}
              onChange={(event) => setMovies(event.target.value)}
            />
          </div>
          <div>
            <label htmlFor="poster">URL du films </label>
            <input
              id="poster"
              name="poster"
              type="text"
              value={movies.poster}
              onChange={(event) => setMovies(event.target.value)}
            />
          </div>
          <div>
            <label htmlFor="comment"> commentaire </label>
            <textarea
              id="comment"
              name="comment"
              type="text"
              placeholder="commentaire"
              value={movies.comment}
              onChange={(event) => setMovies(event.target.value)}
            />
          </div>
          <div>
            <input
              style={{
                width: "20%",
                marginLeft: "43%",
                backgroundColor: "blue",
                color: "white",
                fontWeight: "bolder",
                fontSize: "large",
                cursor: "pointer",
              }}
              type="submit"
              value="Envoyer"
            />
          </div>
        </fieldset>
      </form>
    </div>
  );
};

export default FormMovie;
