import React, { Component } from "react";

class FormMovie extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      poster: "",
      comment: "",
    };
  }

  render() {
    return (
      <div>
        <form>
          <fieldset
            style={{ width: "50%", marginLeft: "25%", marginTop: "20%" }}
          >
            <legend>Ajouter un film</legend>
            <div>
              <label htmlFor="nameMovie">Nom du Film </label>
              <input type="text" id="nameMovie" name="nameMovie" />
            </div>
            <div>
              <label htmlFor="email">URL du films </label>
              <input id="email" name="email" type="email" />
            </div>
            <div>
              <label htmlFor="coment"> commentaire </label>
              <textarea id="coment" type="text" placeholder="commentaire" />
            </div>
            <div>
              <input
                style={{
                  width: "20%",
                  marginLeft: "43%",
                  backgroundColor: "blue",
                  color: "white",
                  fontWeight: "bolder",
                  fontSize: "large",
                  cursor: "pointer"
                }}
                type="submit"
                value="Envoyer"
              />
            </div>
          </fieldset>
        </form>
      </div>
    );
  }
}

export default FormMovie;
