import React, { Component } from "react";

class FormMovie extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      poster: "",
      comment: "",
    };
  }

  render() {
    return (
      <div>
        <form>
          <fieldset>
            <legend>Ajouter un film</legend>
            <div>
              <label htmlFor="nameMovie">Nom du Film </label>
              <input type="text" id="nameMovie" name="nameMovie" />
            </div>
            <div>
              <label htmlFor="email">URL du film </label>
              <input id="email" name="email" type="email" />
            </div>
            <div>
              <textarea type="text" placeholder = "commentaire">Ajoute un commentaire</textarea>
            </div>
          </fieldset>
        </form>
      </div>
    );
  }
}

export default FormMovie;
